import React from 'react';
import './App.css';
import {allCards} from './services/cardService'

interface Card {
  title : string;
  author : string;
  type : string;
  category : string;
  day : number;
  month : number;
  year : number;
}

async function loadNames() {
  const response = await fetch('https://people.canonical.com/~anthonydillon/wp-json/wp/v2/posts.json');
  const names = await response.json();
  console.log(names); 
  const result = Object.entries(names);
  console.log(result)
  for (const k in result) {
    const b = result[k];
    //console.log(b);
    const resu = Object.entries(b);
    const text: any = resu[1][1];
    const text1: any = text['title']['rendered']
    console.log(text1)
  }
  // logs [{ name: 'Joker'}, { name: 'Batman' }]
}
//loadNames();
//allCards();

function App() {
  let cards : Promise<Array<Card> | string> = allCards();
  let showCards = [];
  let length = 0;
  for (const key in cards) {
    length += 1;
  }

  for (let i = 0; i < 3; i++) {
    showCards.push(
    <div className='col-4'>
    <hr className="top-line" />
    <div className="p-card--highlighted col-4" >
      <header className='p-card__header'>People and culture</header>
      <img src="https://ubuntu.com/wp-content/uploads/5288/Title-Cards.jpg" className='p-card__image' alt="" />
      <h3>
        <a href="https://ubuntu.com/blog/travel-clis-and-sticky-notes-lilyanas-life-as-a-canonical-ux-designer/">We'd love to have you join us as a partner</a>
      </h3>
      <p>
        <em>
          by <a href="https://ubuntu.com/blog/author/cmoullec/">cmoullec</a> on 24 August 2020
        </em>
      </p>
      <hr />
      <p className="p-card__content">Article</p>
    </div>
    </div>
  );
  }
  console.log(length);

  return <>
    <div className='row'>
      {showCards}
    </div>
    </>

}

export default App;
