export const AsyncAwait = () => {
    const [users, setUsers] = useState([])

    const fetchData = async () => {
        const response = await fetch("https://people.canonical.com/~anthonydillon/wp-json/wp/v2/posts.json");
        const data = await response.json();
        setUsers(data);
        }

    useEffect(() => {
        fetchData()
        }, [])

    let cards : Card[] = [];
    for (const item in users) {
        const card : any = users[item]
        let cat : string;
        if (card['_embedded']['wp:term'][2].length === 0) {
            cat = 'Announcements'
        } else {
            cat = card['_embedded']['wp:term'][2][0]['name'];
        }
        cards.push({
            title: card['title']['rendered'], 
            author: card['_embedded']['author'][0]['name'],
            type: card['_embedded']['wp:term'][0][0]['name'],
            category: cat,
            day: +card['_end_day'],
            month: +card['_end_month'],
            year: +card['_end_year']
        });
    }
    console.log(cards)

    return ( 
        <div className='row u-equal-height u-clearfix'>
            {cards.map(item =>
                <div className="col-4">
                <hr className="top-line" />
                <div className="p-card--highlighted" >
                <header className='p-card__header'>{item.category}</header>
                <img src="https://ubuntu.com/wp-content/uploads/5288/Title-Cards.jpg" className='p-card__image' alt="" />
                <h3>
                    <a href="https://ubuntu.com/blog/travel-clis-and-sticky-notes-lilyanas-life-as-a-canonical-ux-designer/">{item.title}</a>
                </h3>
                <p className="">
                    <em>
                    by <a href="https://ubuntu.com/blog/author/cmoullec/">{item.author}</a> on {item.day} {monthNames[item.month - 1]} {item.year}
                    </em>
                </p>
                <hr className="bottom-line"/>
                <p className="p-card__content">{item.type}</p>
                </div>
                </div>)}
        </div>

    )
}
