import React, { useEffect, useState } from "react"

interface Card {
    title : string;
    author : string;
    type : string;
    category : string;
    day : number;
    month : number;
    year : number;
}

const getResource = async () => {
    const res = await fetch('https://people.canonical.com/~anthonydillon/wp-json/wp/v2/posts.json');
    const  data  = await res.json();
    return data;
}

export const allCards = async() => {
    const result = await getResource();
    let cards : Card[] = [];
    for (const item in result) {
        const card = result[item]
        cards.push({
            title: card['title']['rendered'], 
            author: card['_embedded']['author'][0]['name'],
            type: card['_embedded']['wp:term'][0][0].name,
            category: card['_embedded']['wp:term'][2][0]?.name.toUpperCase(),
            day: +card['_end_day'],
            month: +card['_end_month'],
            year: +card['_end_year']
        });
    }
    //console.log(cards);
    return cards;
}

